import { Component } from '@angular/core';
import { vrInfo } from 'src/vrInfo';

@Component({
  selector: 'app-vr-custom',
  templateUrl: './vr-custom.component.html',
  styleUrls: ['./vr-custom.component.css']
})
export class VrCustomComponent {
  headset: vrInfo = {
    id: 1,
    ipAddress: '10.101.0.192',
    port: '8001',
    name: 'VR1',
    isOnline: true
  };

  imagePathQuest3: string = 'assets/images/metaquest3.png';
  imagePathDownload: string = 'assets/images/download_button.png';

  sendDownloadAssetsOSC() {
    const url = "http://localhost:3000/DownloadAssets";
    const data = { ipAddress: this.headset.ipAddress,  port: this.headset.port};

    fetch(url, {
      method: 'POST', // or 'PUT'
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success! :', data);
    })
    .catch((error) => {
      console.error('Error! :', error);
    });
  }
}

<h1>
    Custom VR Configuration
</h1>

<ul class="headsets">
    <li>
        <div class="image-container-vr">
          <img [src]="imagePathQuest3" alt="MetaQuest3">
        </div>

        <div>
            <span>Name: {{ headset.name }}</span>
        </div>

        <div>
            <span>IpAdress: {{ headset.ipAddress }}</span>
        </div>

        <div>
            <span>Port: {{ headset.port }}</span>
        </div>

        <div>
            <!--We aply the select class to this item, whenever === --->
            <span>Status: {{ headset.isOnline }}</span>    
        </div>

        <div class="image-container">
            <button (click)="sendDownloadAssetsOSC()">
                <img [src]="imagePathDownload" alt="Click here to download VRHeadset assets">
            </button>
        </div>


    </li>
</ul>
