<h1>Saved VR List</h1>

<ul class="headsets">
    <li *ngFor="let headset of headsetsList" [ngClass]="{'isOnline': headset.isOnline}">
        <span class="badge">{{ headset.name }}</span>

        <div class="image-container-vr">
          <img [src]="headset.isOnline ? imagePathQuest3 : imagePathQuest3Grey" alt="MetaQuest3">
        </div>

        <div>
            <span class = "name" >Name: </span>
            <input type="text" [(ngModel)]="headset.name">
        </div>


        <div>
            <span>IpAddress: </span>
            <input type="text" [(ngModel)]="headset.port">

        </div>

        <div>
            <span>Port: </span>
            <input type="text" [(ngModel)]="headset.port">
        </div>

        <div>
            <!--We aply the select class to this item, whenever === --->
            <span>Status: {{ headset.isOnline }}</span>

        </div>

        <div class="image-container">
            <button (click)="sendOSC()">
                <img [src]="imagePathDownload" alt="Click here to download VRHeadset assets">
            </button>
        </div>
    </li>


    <div class="image-container-button">
        <img [src]="imagePathPlay" alt="Click here to Start Experience">
    </div>

    <!-- Gestao do observable -->
    <div>{{data$ | async | json}} </div> 

</ul>
