import {Component, OnInit} from '@angular/core';
 import OSC from 'osc-js';
// import { Client, Message } from 'node-osc';
// var osc = require("osc");

'use strict';

@Component({
  selector: 'app-heroes',
  templateUrl: './heroes.component.html',
  styleUrls: ['./heroes.component.css']
})
export class HeroesComponent implements OnInit {

  ngOnInit() {

  }

  sendOSC(){
    // Instantiate the BridgePlugin with desired options
    const plugin = new OSC.BridgePlugin({
      udpServer: {
          host: '192.168.1.244', // Specify the specific IP address here
          port: 8001 // Adjust port as needed
      }
    });
    const osc = new OSC({ plugin: plugin })
    osc.open({ host: '192.168.1.244', port: 8001 })
    const message = new OSC.Message('/test', 51.23, "miauString", 665)
    osc.send(message)
  }
}
